import { createSlice } from "@reduxjs/toolkit";

const counterSlice = createSlice({
  name: "counter",
  initialState: {
    value: 0,
  },
  reducers: {
    addToCart(state) { 
     state.value ++;
    },
    removeFromCart(state) {
      state.value += 1;
    },
  },
});

export const { addToCart, removeFromCart } = counterSlice.actions;
export default counterSlice.reducer;
